        return_code = process.returncode

        if return_code != 0:
            error_msg = f"Command failed with return code {return_code}\nCommand: {' '.join(command)}\nStdout: {stdout.decode()}\nStderr: {stderr.decode()}"
            raise Exception(error_msg)

        return stdout, stderr

    except FileNotFoundError:
        raise Exception(f"Command not found: {command[0]}") from None
    except PermissionError:
        raise Exception(f"Permission denied executing command: {command}") from None
    except Exception as e:
        raise Exception(f"Unexpected error executing command: {e}") from None

