            "modelName": self.model_type,
        }

        data = {
            "instance": json.dumps({
                "conversation_id": str(uuid.uuid4()),
                "messages": messages,
            }),
            "params": {
                "do_sample": {"type": "bool", "value": "true"},
                "max_tokens_to_generate": {
                    "type": "int",
                    "value": str(self.model_config_dict.get("max_tokens")),
                },
                "process_prompt": {"type": "bool", "value": "true"},
                "repetition_penalty": {
                    "type": "float",
                    "value": str(self.model_config_dict.get("repetition_penalty")),
                },
                "return_token_count_only": {"type": "bool", "value": "false"},
                "select_expert": {
                    "type": "str",
                    "value": self.model_type.split('/')[1],
                },
                "stop_sequences": {
                    "type": "str",
                    "value": self.model_config_dict.get("stop_sequences"),
                },
