import time
from typing import Optional, Type, Any

class HSFZSocket:
    def __init__(self, ip='127.0.0.1', port=6801):
        self.ip = ip
        self.port = port
        self.s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.s.setsockopt(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)
        self.s.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self.s.connect((self.ip, self.port))
        self.buffer = b""

    def send(self, data):
        try:
            return self.s.send(data)
        except Exception as e:
            self.close()
            raise e

    def recv(self, x=65535):
        if self.buffer:
            len_data = self.buffer[:4]
        else:
            len_data = self.s.recv(4, socket.MSG_PEEK)
            if len(len_data) != 4:
                return None

        len_int = struct.unpack(">I", len_data)[0]
        len_int += 6
        self.buffer += self.s.recv(len_int - len(self.buffer))

        if len(self.buffer) != len_int:
            return None

        pkt = self.buffer
        self.buffer = b""
        return pkt

    def close(self):
        self.s.close()

class UDS_HSFZSocket(HSFZSocket):
    def __init__(self, source, target, ip='127.0.0.1', port=6801, basecls=None):
        super().__init__(ip, port)
        self.source = source
        self.target = target
        self.outputcls = basecls

    def send(self, x):
        try:
            x.sent_time = time.time()
        except AttributeError:
            pass

        try:
            return super().send(
                struct.pack("!IBB", len(x) + 2, 1, self.source, self.target) + bytes(x))
        except Exception as e:
            self.close()
            raise e

    def recv(self, x=65535):
        pkt = super().recv(x)
        if pkt and self.outputcls:
            return self.outputcls(pkt[6:])
        return pkt

