            self._closed = True

    async def recv(self, size: int) -> ReceiveEvent:
        return await ReceiveEvent(self, size)

    async def send(self, data: bytes) -> SendEvent:
        return await SendEvent(self, data)

    async def sendall(self, data: bytes) -> SendEvent:
        return await SendEvent(self, data, sendall=True)

    def readline(self, terminator: bytes = b"\n", bufsize: int = 1024) -> Generator[ReturnEvent, None, None]:
        while True:
            if terminator in self._buf:
                line, sep, rest = self._buf.partition(terminator)
                self._buf = rest
                yield ReturnEvent(line + sep)
                break
            else:
                chunk = yield from self.recv(bufsize)
                if not chunk:
                    yield ReturnEvent(b'')
                    break
                self._buf += chunk
                continue

