        self._message_bytes_out = 0
        self._wire_bytes_in = 0
        self._wire_bytes_out = 0
        self.ping_callback = None
        self.last_ping = 0.0
        self.last_pong = 0.0
        self.close_code = None
        self.close_reason = None

