    def get_subprotocol(self, headers: Dict) -> WebsocketSubProtocols:
        subprotocol = headers.get("Sec-WebSocket-Protocol", "")
        if subprotocol == WebsocketSubProtocols.BYTES.value:
            return WebsocketSubProtocols.BYTES
        return WebsocketSubProtocols.JSON

    async def connect(self, websocket: WebSocket) -> None:
        client = self.get_client(websocket)
        headers = dict(websocket.request_headers)
        self.protocol_dict[client] = self.get_subprotocol(headers)
        self.active_connections.append(websocket)

    def disconnect(self, websocket: WebSocket) -> None:
        client = self.get_client(websocket)
        self.active_connections.remove(websocket)
        if client in self.protocol_dict:
            del self.protocol_dict[client]

    async def receive(self, websocket: WebSocket) -> Any:
        client = self.get_client(websocket)
        protocol = self.protocol_dict.get(client, WebsocketSubProtocols.JSON)

        message = await websocket.recv()
        if protocol == WebsocketSubProtocols.BYTES and isinstance(message, str):
            return bytes.fromhex(message)
        return message

    def __iter__(self, websocket: WebSocket) -> AsyncIterator[Any]:
        async def _iter():
            async for message in websocket:
                client = self.get_client(websocket)
                protocol = self.protocol_dict.get(client, WebsocketSubProtocols.JSON)
                if protocol == WebsocketSubProtocols.BYTES and isinstance(message, str):
                    yield bytes.fromhex(message)
                else:
                    yield message
        return _iter()

    async def send(self, websocket: WebSocket, data: Union["DataRequest", "StatusMessage"]) -> None:
        client = self.get_client(websocket)
        protocol = self.protocol_dict.get(client, WebsocketSubProtocols.JSON)

        if protocol == WebsocketSubProtocols.BYTES and isinstance(data, (bytes, bytearray)):
            await websocket.send(data.hex())
        else:
            await websocket.send(str(data))

