        if not self.api_key:
            return "请先在工具（tools）中输入accuweather的key"

        # Query city location key
        location_api = FreeApi(
            f"http://dataservice.accuweather.com/locations/v1/cities/search?apikey={self.api_key}&q={city}&language=zh-CN&details=false"
        )
        result = location_api.get_result()

