                weather_api = FreeApi(
                    f"http://dataservice.accuweather.com/currentconditions/v1/{city_key}?apikey={self.api_key}&language=zh-CN&details=false"
                )
            elif extensions == "base":
                weather_api = FreeApi(
                    f"http://dataservice.accuweather.com/forecasts/v1/daily/5day/{city_key}?apikey={self.api_key}&language=zh-CN&details=false"
