import queue
from http.client import HTTPConnection, HTTPResponse

class Timeout:
    DEFAULT_TIMEOUT = None

class Retry:
    DEFAULT_RETRY = None

class HTTPConnectionPool:
    scheme = 'http'
    ConnectionCls = HTTPConnection
    ResponseCls = HTTPResponse

    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries if retries is not None else Retry.DEFAULT_RETRY
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        return self.ConnectionCls(self.host, self.port, timeout=self.timeout, proxy=self.proxy, **self.conn_kw)

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(timeout=timeout)
        except queue.Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        conn.request(method, url, timeout=timeout, **httplib_request_kw)
        resp = conn.getresponse()
        return self.ResponseCls(resp)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, **response_kw):
        conn = self._get_conn(pool_timeout)
        retries = retries if retries is not None else self.retries
        while True:
            try:
                response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers)
                if redirect and response.status in (301, 302, 303, 307, 308):
                    redirect_location = response.headers.get('Location')
                    if redirect_location:
                        self._put_conn(conn)
                        conn = None
                        return self.urlopen(method, redirect_location, body, headers, retries, redirect, assert_same_host, timeout, **response_kw)
                self._put_conn(conn)
                return response
            except Exception as e:
                if retries:
                    retries -= 1
                    if conn:
                        self._put_conn(conn)
                    conn = self._get_conn()
                else:
                    if conn:
                        self._put_conn(conn)
                    raise

    def close(self):
        while not self.pool.empty():
            try:
                conn = self.pool.get()
                conn.close()
            except queue.Empty:
                pass

