import os
import requests

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    wav_base = os.path.splitext(os.path.basename(wav_path))[0]
    txt_base = os.path.splitext(os.path.basename(txt_path))[0]
    if wav_base != txt_base:
        print("Error: WAV and TXT file names do not match.")
        return None

    try:
        with open(txt_path, 'r') as txt_file:
            transcript_content = txt_file.read()
    except Exception as e:
        print(f"Error reading transcript file: {e}")
        return None

    try:
        with open(wav_path, 'rb') as wav_file:
            files = {
                'audio': (os.path.basename(wav_path), wav_file, 'audio/wav'),
                'transcript': (os.path.basename(txt_path), transcript_content, 'text/plain')
            }
            url = f"http://{server_addr}:{port}"
            response = requests.post(url, files=files)
    except Exception as e:
        print(f"Error sending request: {e}")
        return None

    if response.status_code != 200:
        print(f"Server returned error: {response.status_code} - {response.text}")
        return None

    if debug:
        print(response.json())

    return response

