class Fetcher:
    def __init__(self, base_url: str, api_key: str) -> None:
        self._base_url = base_url
        self._api_key = api_key
        self._stop_polling_event = threading.Event()
        self._exit_monitor = None
        self._poller_thread = None

    def run(self) -> None:
        self._exit_monitor = threading.Thread(
            target=Fetcher._monitor_exit,
            args=(self._stop_polling_event,)
        )
        self._exit_monitor.daemon = True
        self._exit_monitor.start()

        self._poller_thread = threading.Thread(
            target=Fetcher._thread_func,
            args=(self._base_url, self._api_key, self._stop_polling_event, 60)
        )
        self._poller_thread.start()

    def post(self, api: str, body: Dict[str, str]) -> None:
        url = f"{self._base_url}/{api}"
        Fetcher._post_url(url, self._api_key, body)

    @staticmethod
    def _fetch_url(url: str, api_key: str) -> Dict:
        retries = 5
        backoff_factor = 1
        for attempt in range(retries):
            try:
                response = requests.get(url, headers={'Authorization': f'Bearer {api_key}'}, timeout=10)
                if response.status_code >= 500:
                    if attempt < retries - 1:
                        time.sleep(backoff_factor * (2 ** attempt))
                        continue
                    else:
                        response.raise_for_status()
                response.raise_for_status()
                return response.json()
            except RequestException as e:
                if attempt < retries - 1:
                    time.sleep(backoff_factor * (2 ** attempt))
                else:
                    raise
        return {}

    @staticmethod
    def _post_url(url: str, api_key: str, body: Dict[str, str]) -> None:
        retries = 5
        backoff_factor = 1
