            try:
                response = requests.post(url, headers={'Authorization': f'Bearer {api_key}'}, json=body, timeout=10)
                if response.status_code >= 500:
                    if attempt < retries - 1:
                        time.sleep(backoff_factor * (2 ** attempt))
                        continue
                    else:
                        response.raise_for_status()
                response.raise_for_status()
                return
            except RequestException as e:
                if attempt < retries - 1:
                    time.sleep(backoff_factor * (2 ** attempt))
                else:
                    raise

    @staticmethod
    def _thread_func(base_url: str, api_key: str, stop_polling_event: threading.Event, seconds_interval: Optional[int]) -> None:
        interval = seconds_interval if seconds_interval is not None else 60
        while not stop_polling_event.wait(interval):
