        elif isinstance(raw_image_data, dict):
            raise TypeError("Dict image format not supported")
        else:
            raise TypeError(f"Unsupported image type: {type(raw_image_data)}")

    def generate(self, text_prompt: str, raw_image_data: Union[Image.Image, dict, str]) -> str:
        pil_image = self._get_pil_image(raw_image_data)
