        try:
            response = requests.post(url, data=data)
            if not response.ok:
                logging.error(f"Failed to send message. Status code: {response.status_code}, Response: {response.text}")
            elif not response.json().get("ok", False):
                logging.error(f"Telegram API error: {response.json().get('description', 'Unknown error')}")
        except Exception as e:
            logging.exception(f"Exception occurred while sending message: {e}")

    def send_file(self, file_name: str, contents: bytes) -> None:
        converted_contents = contents
        new_file_name = file_name
        if file_name.lower().endswith(".svg"):
            try:
                import cairosvg
                converted_contents = cairosvg.svg2png(bytestring=contents)
                if "." in file_name:
                    new_file_name = file_name.rsplit(".", 1)[0] + ".png"
                else:
                    new_file_name += ".png"
            except ImportError:
                logging.error("cairosvg library is not installed. Cannot convert SVG to PNG. Sending original SVG as document.")
            except Exception as e:
                logging.error(f"Failed to convert SVG to PNG: {e}. Sending original SVG as document.")
                new_file_name = file_name
                converted_contents = contents

        file_ext = new_file_name.split(".")[-1].lower() if "." in new_file_name else ""
        image_extensions = {"png", "jpg", "jpeg", "gif", "bmp", "webp"}
        is_image = file_ext in image_extensions

        if is_image:
            url = f"https://api.telegram.org/bot{self.bot_token}/sendPhoto"
            file_param = "photo"
        else:
            url = f"https://api.telegram.org/bot{self.bot_token}/sendDocument"
            file_param = "document"

        data = {"chat_id": self.chat_id}
        if self.thread_id:
            data["message_thread_id"] = self.thread_id

        files = {file_param: (new_file_name, converted_contents)}
        try:
            response = requests.post(url, data=data, files=files)
            if not response.ok:
                logging.error(f"Failed to send file. Status code: {response.status_code}, Response: {response.text}")
            elif not response.json().get("ok", False):
                logging.error(f"Telegram API error: {response.json().get('description', 'Unknown error')}")
