from typing import Optional, Dict, Any, List, Union

class Timeout:
    DEFAULT_TIMEOUT = socket._GLOBAL_DEFAULT_TIMEOUT
    DEFAULT = None

class Retry:
    DEFAULT = None

class HTTPConnectionPool:
    scheme = 'http'
    ConnectionCls = HTTPConnection
    ResponseCls = HTTPResponse

    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries if retries is not None else Retry.DEFAULT
        self.pool = queue.LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw.copy() if conn_kw else {}
        self.headers = headers.copy() if headers else {}

    def _new_conn(self):
        conn_params = self.conn_kw.copy()
        if self.proxy:
            proxy_host, proxy_port = self.proxy.split(':')[0], int(self.proxy.split(':')[1]) if ':' in self.proxy else 80
            conn = self.ConnectionCls(proxy_host, proxy_port, timeout=self.timeout, **conn_params)
            if self.scheme == 'https':
                conn.set_tunnel(self.host, self.port, self.proxy_headers)
        else:
            conn = self.ConnectionCls(self.host, self.port, timeout=self.timeout, **conn_params)
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=True, timeout=timeout)
        except queue.Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        if timeout != Timeout.DEFAULT_TIMEOUT:
            conn.timeout = timeout
        try:
            conn.request(method, url, **httplib_request_kw)
            return conn.getresponse()
        except socket.timeout:
            conn.close()
            raise
        except Exception:
            conn.close()
            raise

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, **response_kw):
        retries = retries if retries is not None else self.retries
        conn = self._get_conn(pool_timeout)
        try:
            resp = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers)
            if redirect and resp.status in (301, 302, 303, 307, 308):
                location = resp.getheader('Location')
                if location and (not assert_same_host or self.host == location.split('/')[2].split(':')[0]):
                    if release_conn is None:
                        release_conn = True
                    if release_conn:
                        self._put_conn(conn)
                    return self.urlopen(method, location, body=body, headers=headers, retries=retries.decrement(), redirect=redirect, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, **response_kw)
            return resp
        except Exception as e:
            if retries and retries.is_retryable_error(e):
                return self.urlopen(method, url, body=body, headers=headers, retries=retries.decrement(), redirect=redirect, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, **response_kw)
            conn.close()
