class ServeTokenizeResult(BaseModel):
    tokens: List[str]
    token_ids: Optional[List[int]] = None

class ServeDeTokenizeResult(BaseModel):
    text: str

class ServeForwardResult(BaseModel):
    outputs: Any

class ServeCommand:
    def __init__(self, pipeline, host: str = "localhost", port: int = 8888, workers: int = 1):
        self._pipeline = pipeline
        self.host = host
        self.port = port
        self.workers = workers
        self._app = FastAPI()

        @self._app.get("/model_info")
        async def model_info():
            return self.model_info()

        @self._app.post("/tokenize")
        async def tokenize(text_input: str = Body(None, embed=True), return_ids: bool = Body(False, embed=True)):
            return self.tokenize(text_input, return_ids)

        @self._app.post("/detokenize")
        async def detokenize(tokens_ids: List[int] = Body(None, embed=True), skip_special_tokens: bool = Body(False, embed=True), cleanup_tokenization_spaces: bool = Body(True, embed=True)):
            return self.detokenize(tokens_ids, skip_special_tokens, cleanup_tokenization_spaces)

        @self._app.post("/forward")
        async def forward(inputs: Any = Body(None, embed=True)):
            return self.forward(inputs)

    def run(self):
        uvicorn.run(self._app, host=self.host, port=self.port, workers=self.workers)

    def model_info(self):
        info = {
            "model_name": self._pipeline.model.__class__.__name__,
            "tokenizer_name": self._pipeline.tokenizer.__class__.__name__
        }
        return ServeModelInfoResult(info=info)

    def tokenize(self, text_input: str, return_ids: bool):
        tokens = self._pipeline.tokenizer.tokenize(text_input)
        token_ids = self._pipeline.tokenizer.convert_tokens_to_ids(tokens) if return_ids else None
        return ServeTokenizeResult(tokens=tokens, token_ids=token_ids)

    def detokenize(self, tokens_ids: List[int], skip_special_tokens: bool, cleanup_tokenization_spaces: bool):
        text = self._pipeline.tokenizer.decode(tokens_ids, skip_special_tokens=skip_special_tokens, clean_up_tokenization_spaces=cleanup_tokenization_spaces)
        return ServeDeTokenizeResult(text=text)

    def forward(self, inputs):
        outputs = self._pipeline(**inputs)
        return ServeForwardResult(outputs=outputs)

