    bp = Blueprint('api', __name__, url_prefix=url_root)

    @bp.route('/translate', methods=['POST'])
    def translate():
        data = request.get_json()
        if not data:
            return jsonify(error="No JSON payload received"), 400
        text = data.get('text')
        source_lang = data.get('source_lang')
        target_lang = data.get('target_lang')
        if not all([text, source_lang, target_lang]):
            return jsonify(error="Missing required parameters: text, source_lang, target_lang"), 400
        try:
            result = current_app.translation_server.translate(text, source_lang, target_lang)
            return jsonify(translation=result)
        except Exception as e:
            current_app.logger.error(f"Translation failed: {str(e)}", exc_info=True)
            return jsonify(error="Internal server error"), 500

    app.register_blueprint(bp)
    translation_server = TranslationServer(config_file)
    app.translation_server = translation_server

    serve(app, host=host, port=port)

