class InferenceServer:
    def __init__(self, models_dirs=None):
        self.app = Flask(__name__)
        self.app.server = self
        self.api = Api(self.app)
        self.inferencers = {}
        if models_dirs:
            self._load_models(models_dirs)
        self._setup_routes()

    def _load_models(self, models_dirs):
        for idx, model_dir in enumerate(models_dirs):
            inferencer = self._load_inferencer(model_dir)
            self.inferencers[idx + 1] = inferencer

    def _load_inferencer(self, model_dir):
        class DummyInferencer:
            def __init__(self, directory):
                self.directory = directory
                self.metadata = {
                    'name': f"Model_{directory}",
                    'description': 'Dummy model for testing'
                }
            def predict(self, data):
                return {'prediction': 'dummy'}
        return DummyInferencer(model_dir)

    def _setup_routes(self):
        self.api.add_resource(self.ModelListEndpoint, '/models')
        self.api.add_resource(self.InferenceEndpoint, '/infer/<int:model_id>')

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

    class ModelListEndpoint(Resource):
        def get(self):
            server = self.api.app.server
            models = []
            for model_id, inferencer in server.inferencers.items():
                meta = inferencer.metadata.copy()
                meta['id'] = model_id
                models.append(meta)
            return models

    class InferenceEndpoint(Resource):
        def post(self, model_id):
            server = self.api.app.server
            if model_id not in server.inferencers:
                return resp_json({'error': 'Model not found'}, code=404)
            data = request.get_json()
            inferencer = server.inferencers[model_id]
            result = inferencer.predict(data)
            return resp_json(result)

