class ReviewClassifier:
    def __init__(self):
        self.app = Flask(__name__)
        self.clf = pickle.load(open('model.pkl', 'rb'))
        self.vect = pickle.load(open('vectorizer.pkl', 'rb'))
        self.db = 'reviews.db'
        self.setup_routes()

    def setup_routes(self):
        @self.app.route('/')
        def index():
            return render_template('index.html')

        @self.app.route('/results', methods=['POST'])
        def results():
            document = request.form['review']
            sentiment, proba = self.classify(document)
            return render_template('results.html', review=document, sentiment=sentiment, probability=proba)

        @self.app.route('/feedback', methods=['POST'])
        def feedback():
            document = request.form['review']
            y = int(request.form['sentiment'])
            self.train(document, y)
            self.sqlite_entry(document, y)
            return redirect(url_for('index'))

    def classify(self, document):
        X = self.vect.transform([document])
        y_pred = self.clf.predict(X)[0]
        proba = self.clf.predict_proba(X)[0][y_pred]
        return ('positive' if y_pred == 1 else 'negative', proba)

    def train(self, document, y):
        X = self.vect.transform([document])
        if hasattr(self.clf, 'partial_fit'):
            self.clf.partial_fit(X, [y])

    def sqlite_entry(self, document, y):
        conn = sqlite3.connect(self.db)
        c = conn.cursor()
        c.execute('''CREATE TABLE IF NOT EXISTS reviews
                     (review TEXT, sentiment INTEGER, timestamp DATETIME DEFAULT CURRENT_TIMESTAMP)''')
        c.execute('INSERT INTO reviews (review, sentiment) VALUES (?, ?)', (document, y))
        conn.commit()
        conn.close()

    def run(self):
        self.app.run(debug=True)

