from flask_cors import CORS
from langchain.document_loaders import TextLoader, PyPDFLoader, CSVLoader
from langchain.embeddings import HuggingFaceEmbeddings
from langchain.vectorstores import FAISS
from langchain.text_splitter import RecursiveCharacterTextSplitter
from langchain.chains import RetrievalQA
from langchain.llms import GPT4All
import os
from typing import List
from langchain.docstore.document import Document

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        CORS(self.app)
        self.embeddings_model_name = 'all-MiniLM-L6-v2'
        self.persist_directory = 'db'
        self.model_type = 'gpt4all'
        self.model_path = 'models/ggml-gpt4all-j.bin'
        self.model_n_ctx = 512
        self.llm = None
        self._setup_routes()
        self._load_model()

    def _setup_routes(self):
        @self.app.route('/ingest', methods=['GET'])
        def ingest_data():
            return self.ingest_data()

        @self.app.route('/get_answer', methods=['POST'])
        def get_answer():
            return self.get_answer()

        @self.app.route('/download_model', methods=['GET'])
        def download_model():
            return self.download_and_save()

    def _load_documents(self, source_dir: str) -> List[Document]:
        docs = []
        for file in os.listdir(source_dir):
            file_path = os.path.join(source_dir, file)
            try:
                doc = self._load_single_document(file_path)
                docs.append(doc)
            except ValueError:
                continue
        return docs

    def _load_single_document(self, file_path: str) -> Document:
        ext = os.path.splitext(file_path)[1].lower()
        if ext == '.txt':
            loader = TextLoader(file_path)
        elif ext == '.pdf':
            loader = PyPDFLoader(file_path)
        elif ext == '.csv':
            loader = CSVLoader(file_path)
        else:
            raise ValueError(f"Unsupported file type: {file_path}")
        documents = loader.load()
        if not documents:
            raise ValueError(f"No documents loaded from {file_path}")
        return documents[0]

    def ingest_data(self):
        source_dir = 'documents/'
        documents = self._load_documents(source_dir)
        text_splitter = RecursiveCharacterTextSplitter(chunk_size=1000, chunk_overlap=200)
        texts = text_splitter.split_documents(documents)
        embeddings = HuggingFaceEmbeddings(model_name=self.embeddings_model_name)
        db = FAISS.from_documents(texts, embeddings)
        db.save_local(self.persist_directory)
        return jsonify({"status": "success", "message": "Documents processed and saved."})

    def get_answer(self):
        data = request.get_json()
        question = data.get('question')
        if not question:
            return jsonify({"error": "Question is required"}), 400

        embeddings = HuggingFaceEmbeddings(model_name=self.embeddings_model_name)
        db = FAISS.load_local(self.persist_directory, embeddings)
        retriever = db.as_retriever(search_kwargs={"k": 2})
        qa = RetrievalQA.from_chain_type(llm=self.llm, chain_type="stuff", retriever=retriever)
        answer = qa.run(question)
        return jsonify({"answer": answer})

    def download_and_save(self):
        try:
            self._load_model()
            return jsonify({"status": "success", "message": "Model downloaded and saved."})
        except Exception as e:
            return jsonify({"error": str(e)}), 500

    def _load_model(self):
        if not os.path.exists(os.path.dirname(self.model_path)):
            os.makedirs(os.path.dirname(self.model_path), exist_ok=True)
        self.llm = GPT4All(model=self.model_path, n_ctx=self.model_n_ctx)

    def run(self):
        self.app.run(host='0.0.0.0', port=5000)

