from werkzeug.serving import WSGIServer

class RestfulGatewayThread(threading.Thread):
    def __init__(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int) -> None:
        super().__init__()
        self._deployment_name = deployment_name
        self._rest_host = rest_host
        self._rest_port = rest_port
        self._rest_procs = rest_procs
        self._stop_event = threading.Event()
        self.server = None

    def _create_server(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int) -> WSGIServer:
        app = Flask(__name__)

        @app.route('/process', methods=['POST'])
        def process_request():
            data = request.get_json()
            return jsonify({"result": f"Processed by {self._deployment_name}"})

        @app.route('/health', methods=['GET'])
        def health_check():
            return jsonify({"status": "OK"})

        @app.route('/terminate', methods=['POST'])
        def terminate():
            self._stop_event.set()
            self._shutdown()
            return jsonify({"status": "Server shutting down"})

        server = WSGIServer((rest_host, rest_port), app, processes=rest_procs)
        return server

    def _shutdown(self) -> None:
        if self.server:
            self.server.shutdown()

    def run(self) -> None:
        self.server = self._create_server(self._deployment_name, self._rest_host, self._rest_port, self._rest_procs)
        self.server.serve_forever()

    def get_stop_event(self) -> threading.Event:
