class ModelServer:
    def __init__(self, model):
        self.app = Flask(__name__)
        self.model = model
        self.cors = CORS(self.app)
        self.lock = threading.Lock()
        self.setup_routes()

    def setup_routes(self):
        @self.app.route('/stream', methods=['POST'])
        def stream_route():
            return self.stream()

        @self.app.route('/methods', methods=['GET'])
        def methods_route():
            return self.methods()

