        data = request.get_json()
        def generate():
            with self.lock:
                for chunk in self.model(data):
                    yield chunk
        return Response(generate(), mimetype='text/event-stream')

    def methods(self):
        methods_info = [
            {
                "endpoint": "POST /stream",
                "description": "Stream model responses based on input data."
            },
            {
                "endpoint": "GET /methods",
                "description": "Retrieve information about available API methods."
            }
