@dataclass
class AppConfig:
    use_after_free: bool = False
    ensure_resource_availability: bool = False
    merge_body: bool = True
    independent_500: bool = False
    failure_behind_failure: bool = False
    multiple_conformance_issues: bool = False
    unsatisfiable: bool = False
    custom_headers: dict | None = None
    multiple_source_links: bool = False
    auth_token: str | None = None
    ignored_auth: bool = False
    slowdown: float | int | None = None
    multiple_incoming_links_with_same_status: bool = False
    duplicate_operation_links: bool = False
    circular_links: bool = False
    invalid_parameter: bool = False
    list_users_as_root: bool = False
    no_reliable_transitions: bool = False
    return_plain_text: Literal[False] | str | bytes = False
    omit_required_field: bool = False

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.config = AppConfig()
        self.next_user_id = 1
        self.last_modified = "2021-01-01T00:00:00Z"
        self.users = {0: {"id": 0, "name": "John Doe", "last_modified": self.last_modified}}
        self.deleted_orders = set()
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route("/openapi.json", methods=["GET"])
        def get_spec():
            return jsonify(self.app.config["schema"])

        @self.app.route("/users/<int:user_id>", methods=["GET"])
        def get_user(user_id):
            if self.config.slowdown:
                time.sleep(self.config.slowdown)
            user = self.users.get(user_id)
            if user:
                if self.config.return_plain_text is not False:
                    return self.config.return_plain_text, 200, {"Content-Type": "text/plain"}
                if self.config.omit_required_field:
                    return jsonify({"name": user["name"], "last_modified": user["last_modified"]})
                return jsonify(user)
            return jsonify({"error": "User not found"}), 404

        @self.app.route("/users", methods=["GET"])
        def list_users():
            return jsonify(self.users)

        @self.app.route("/users", methods=["POST"])
        def create_user():
            if self.config.slowdown:
                time.sleep(self.config.slowdown)
            data = request.get_json()
            if not isinstance(data, dict):
                return jsonify({"error": "Invalid input"}), 400
            name = data.get("name")
            if name is None:
                return jsonify({"error": "Name is required"}), 400

            new_user = {"id": self.next_user_id, "name": name, "last_modified": self.last_modified}
            if not self.config.ensure_resource_availability:
                self.users[self.next_user_id] = new_user
            self.next_user_id += 1

            if self.config.omit_required_field:
                return jsonify({"name": new_user["name"], "last_modified": new_user["last_modified"]}), 201
            return jsonify(new_user), 201

        @self.app.route("/users/<int:user_id>", methods=["PATCH"])
        def update_user(user_id):
            if self.config.slowdown:
                time.sleep(self.config.slowdown)
            user = self.users.get(user_id)
            if user:
                data = request.get_json()
                if not self.config.merge_body:
                    assert len(data) == 1
                else:
                    assert "name" in data
                    user["name"] = data["name"]
                return jsonify(user)
            return jsonify({"error": "User not found"}), 404

        @self.app.route("/users/<int:user_id>", methods=["DELETE"])
        def delete_user(user_id):
            if self.config.slowdown:
                time.sleep(self.config.slowdown)
            user = self.users.get(user_id)
            if user:
                if self.config.use_after_free:
                    if len(user["name"]) < 10:
                        del self.users[user_id]
                else:
                    del self.users[user_id]
                return jsonify({"message": "User deleted successfully"}), 204
            return jsonify({"error": "User not found"}), 404

        @self.app.route("/orders/<order_id>", methods=["DELETE"])
        def delete_order(order_id):
            if self.config.slowdown:
                time.sleep(self.config.slowdown)
            if order_id in self.deleted_orders:
                return jsonify({"error": "Order not found"}), 404
            self.deleted_orders.add(order_id)
            return jsonify({"message": "Nothing happened"}), 200

        @self.app.before_request
        def check_auth():
            if not (self.config.ignored_auth or self.config.auth_token is None or request.endpoint == get_spec.__name__):
                auth_header = request.headers.get("Authorization")
                if not auth_header:
                    abort(401, description="Authorization header is missing")
                try:
                    token_type, token = auth_header.split()
                    if token_type.lower() != "bearer" or token != self.config.auth_token:
                        abort(401, description="Invalid token")
                except ValueError:
                    abort(401, description="Invalid Authorization header format")

