        @self.app.route('/orders/<order_id>', methods=['DELETE'])
        def delete_order_route(order_id):
            return self.delete_order(order_id)

    def check_auth(self):
        if self.config.require_auth:
            auth = request.headers.get('Authorization')
            if not auth:
                return jsonify({'error': 'Unauthorized'}), 401

    def get_spec(self):
        spec = """
        openapi: 3.0.0
        info:
          title: User Management API
          version: 1.0.0
        paths:
          /users:
            get:
              summary: List all users
              responses:
                '200':
                  description: A list of users
            post:
              summary: Create a new user
              requestBody:
                required: true
                content:
                  application/json:
                    schema:
                      type: object
                      properties:
                        name:
                          type: string
                        email:
                          type: string
              responses:
                '201':
                  description: User created successfully
          /users/{user_id}:
            get:
              summary: Get a user by ID
              responses:
                '200':
                  description: User data
                '404':
                  description: User not found
            put:
              summary: Update a user
              responses:
                '200':
                  description: User updated
                '404':
                  description: User not found
            delete:
              summary: Delete a user
              responses:
                '204':
                  description: User deleted
                '404':
                  description: User not found
          /orders/{order_id}:
            delete:
              summary: Delete an order
              responses:
                '200':
                  description: Order deleted
        """
        return spec, 200, {'Content-Type': 'text/yaml'}

    def get_user(self, user_id):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        if user_id not in self.users:
            return jsonify({'error': 'User not found'}), 404
        return jsonify(self.users[user_id]), 200

    def list_users(self):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        return jsonify(list(self.users.values())), 200

    def create_user(self):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        try:
            data = request.get_json()
            if self.config.validate_input and ('name' not in data or 'email' not in data):
                return jsonify({'error': 'Invalid input'}), 400
            user_id = self.next_user_id
            self.next_user_id += 1
            self.users[user_id] = data
            self.last_modified = datetime.datetime.now().isoformat()
            return jsonify({'id': user_id, **data}), 201
        except Exception as e:
            return jsonify({'error': 'Invalid data'}), 400

    def update_user(self, user_id):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        if user_id not in self.users:
            return jsonify({'error': 'User not found'}), 404
        data = request.get_json()
        self.users[user_id].update(data)
        self.last_modified = datetime.datetime.now().isoformat()
        return jsonify(self.users[user_id]), 200

    def delete_user(self, user_id):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        if user_id not in self.users:
            return jsonify({'error': 'User not found'}), 404
        del self.users[user_id]
        return '', 204

    def delete_order(self, order_id):
        if self.config.inject_error:
            return jsonify({'error': 'Internal Server Error'}), 500
        self.deleted_orders.add(order_id)
        return jsonify({'message': 'Order deleted'}), 200

