    def query_db(self, query: str, args: tuple = (), one: bool = False) -> Union[List, Dict, None]:
        cur = g.db.cursor()
        cur.execute(query, args)
        result = cur.fetchall()
        cur.close()
        return result[0] if (one and result) else result

    def get_user_id(self, username: str) -> Union[int, None]:
        result = self.query_db('SELECT id FROM users WHERE username = ?', (username,), one=True)
        return result['id'] if result else None

    def papers_filter_version(self, papers: list, v: str) -> list:
        return [p for p in papers if p['version'] == v]

    def default_context(self, papers: list, **kws) -> dict:
        return {'papers': papers, **kws}

    def run(self, port: int = 5000, debug: bool = False, prod: bool = False) -> None:
        if prod:
            try:
                from waitress import serve
                serve(self.app, host='0.0.0.0', port=port)
            except ImportError:
                self.app.run(host='0.0.0.0', port=port, debug=debug)
        else:
            self.app.run(port=port, debug=debug)

