import socket
import ssl
from urllib.parse import urlparse
import threading
from collections import deque

class Timeout:
    DEFAULT_TIMEOUT = socket.getdefaulttimeout()

class Retry:
    def __init__(self, total=3):
        self.total = total

    def decrement(self):
        return Retry(self.total - 1)

class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
