        self.maxsize = maxsize
        self.block = block
        self.headers = headers or {}
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw.copy() if conn_kw else {}
        self.pool = deque()
        self.pool_lock = threading.Lock()
        self.scheme = self.conn_kw.pop('scheme', 'http')

    def _new_conn(self):
        if self.scheme == 'https':
            conn = http.client.HTTPSConnection(self.host, self.port, **self.conn_kw)
        else:
            conn = http.client.HTTPConnection(self.host, self.port, **self.conn_kw)
        if self.proxy:
            proxy_url = urlparse(self.proxy)
            proxy_host, proxy_port = proxy_url.hostname, proxy_url.port
            conn.set_tunnel(self.host, self.port, self.proxy_headers)
        return conn

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, chunked=False, **httplib_request_kw):
        if timeout != Timeout.DEFAULT_TIMEOUT:
            conn.timeout = timeout
        try:
            conn.request(method, url, headers=httplib_request_kw.get('headers', {}))
            return conn.getresponse()
        except (socket.timeout, ssl.SSLError, socket.error) as e:
            raise e

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries or self.retries or Retry(total=0)
        while True:
            try:
                conn = self._get_conn(pool_timeout)
                try:
                    response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, **response_kw)
                    if redirect and response.status in (301, 302, 303, 307):
                        location = response.getheader('Location')
                        if location:
                            self._put_conn(conn)
                            url = location
                            method = 'GET'
                            continue
                    self._put_conn(conn)
                    return response
                finally:
                    if release_conn:
                        self._put_conn(conn)
            except Exception as e:
                if retries.total > 0:
                    retries = retries.decrement()
                    continue
                else:
                    raise

    def _get_conn(self, timeout=None):
        with self.pool_lock:
            if self.pool:
                return self.pool.popleft()
        return self._new_conn()

    def _put_conn(self, conn):
        with self.pool_lock:
            if len(self.pool) < self.maxsize:
                self.pool.append(conn)
            else:
                conn.close()

