        if 'file' not in request.files:
            return 'No file uploaded', 400
        file = request.files['file']
        if file.filename == '':
            return 'No selected file', 400
        try:
            model = onnx.load(file.stream)
            self.onnx_modifier = onnxModifier(model)
            return 'Model loaded successfully', 200
        except Exception as e:
            return str(e), 500

    def modify_and_download_model(self):
        if not self.onnx_modifier:
            return 'No model loaded', 400
        try:
            data = request.get_json()
            self.onnx_modifier.apply_modifications(data)
            output = BytesIO()
            onnx.save(self.onnx_modifier.model, output)
            output.seek(0)
            return send_file(output, as_attachment=True, download_name='modified_model.onnx', mimetype='application/octet-stream')
        except Exception as e:
            return str(e), 500

    def run(self, host='127.0.0.1', port=5000, debug=False):
        self.app.run(host=host, port=port, debug=debug)

