app = Flask(__name__)

sessions = {}

@app.route('/')
def index():
    return "Main Page"

@app.route('/get_qrcode', methods=['GET'])
def get_qrcode():
    sid = str(uuid.uuid4())
    auth_code = ''.join(random.choices(string.ascii_uppercase + string.digits, k=10))
    sessions[sid] = {
        'status': 'pending',
        'auth_code': auth_code
    }
    qr = qrcode.make(auth_code)
    img_io = BytesIO()
    qr.save(img_io, 'PNG')
    img_data = base64.b64encode(img_io.getvalue()).decode('utf-8')
    qr_url = f"data:image/png;base64,{img_data}"
    return jsonify(qr_url=qr_url, sid=sid)

@app.route('/check_qrcode/<sid>', methods=['GET'])
def check_qrcode(sid):
    session = sessions.get(sid)
    if not session:
        return jsonify({'status': 'error', 'message': 'Invalid session ID'}), 404
    return jsonify({'sid': sid, 'status': session['status']})

@app.route('/get_tokens', methods=['POST'])
def get_tokens():
    data = request.get_json()
    auth_code = data.get('auth_code')
    if not auth_code:
        return jsonify({'status': 'error', 'message': 'Authentication code missing'}), 400

    for sid, session in sessions.items():
        if session['auth_code'] == auth_code:
            if session['status'] == 'pending':
                sessions[sid]['status'] = 'used'
                return jsonify({'status': 'success', 'message': 'Tokens retrieved'})
            else:
                return jsonify({'status': 'error', 'message': 'Authentication code already used'}), 400
    return jsonify({'status': 'error', 'message': 'Invalid authentication code'}), 400

if __name__ == '__main__':
