import socket
from typing import Optional, Mapping, List, Tuple, Any
from http.client import HTTPConnection, HTTPResponse, _TYPE_TIMEOUT, _DEFAULT_TIMEOUT, _TYPE_BODY
from urllib3.util.url import Url
from urllib3.util.proxy import ProxyConfig

_TYPE_SOCKET_OPTIONS = List[Tuple[int, int, Any]]

class HTTPConnection(HTTPConnection):
    default_port: int = 80
    default_socket_options: _TYPE_SOCKET_OPTIONS = [
        (socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)
    ]

    def __init__(self, host: str, port: Optional[int] = None, *, timeout: _TYPE_TIMEOUT = _DEFAULT_TIMEOUT, source_address: Optional[Tuple[str, int]] = None, blocksize: int = 16384, socket_options: Optional[_TYPE_SOCKET_OPTIONS] = None, proxy: Optional[Url] = None, proxy_config: Optional[ProxyConfig] = None) -> None:
        super().__init__(host, port, timeout=timeout, source_address=source_address, blocksize=blocksize)
        self.socket_options = socket_options if socket_options is not None else self.default_socket_options
        self.proxy = proxy
        self.proxy_config = proxy_config
        self._has_connected_to_proxy = False
        self._response_options = None
        self._tunnel_host = None
        self._tunnel_port = None
        self._tunnel_scheme = None

    def _new_conn(self) -> socket.socket:
        if self.proxy:
            host = self.proxy.host
            port = self.proxy.port if self.proxy.port is not None else self.default_port
        else:
            host = self.host
            port = self.port if self.port is not None else self.default_port
        try:
            sock = socket.create_connection(
                (host, port),
                timeout=self.timeout,
                source_address=self.source_address
            )
        except (socket.error, OSError) as e:
            raise ConnectionError(f"Failed to connect to {host}:{port}") from e
        for level, optname, value in self.socket_options:
            sock.setsockopt(level, optname, value)
        return sock

    def connect(self) -> None:
        self.sock = self._new_conn()
        if self._tunnel_host:
            self._tunnel()

    def request(self, method: str, url: str, body: Optional[_TYPE_BODY] = None, headers: Optional[Mapping[str, str]] = None, *, chunked: bool = False, preload_content: bool = True, decode_content: bool = True, enforce_content_length: bool = True) -> None:
        self._response_options = {
            'preload_content': preload_content,
            'decode_content': decode_content,
            'enforce_content_length': enforce_content_length
        }
        headers = dict(headers) if headers else {}
        if 'Host' not in headers:
            headers['Host'] = self.host
        super().request(method, url, body, headers, encode_chunked=chunked)

    def getresponse(self) -> HTTPResponse:
        return super().getresponse()

    def close(self) -> None:
        super().close()
        self._has_connected_to_proxy = False
        self._response_options = None
        self._tunnel_host = None
        self._tunnel_port = None
        self._tunnel_scheme = None

