            required_fields = ["target", "module"]
            if not all(field in data for field in required_fields):
                return jsonify({"error": "Missing required fields"}), 400

            report_filename = f"scan_{data['target'].replace('/', '_')}.json"
            sanitized_path = self._sanitize_report_path_filename(report_filename)
            if not sanitized_path:
                return jsonify({"error": "Invalid report path"}), 400

            command = [
                "nettacker",
                "-t", data["target"],
                "-m", data["module"],
                "-r", str(sanitized_path),
                "--json"
            ]

            try:
                process = subprocess.Popen(
                    command,
                    stdout=subprocess.PIPE,
                    stderr=subprocess.PIPE
                )
                stdout, stderr = process.communicate()

                logs_dir = Path(self.options.report_directory) / "logs"
                logs_dir.mkdir(exist_ok=True)
                log_path = logs_dir / f"{Path(report_filename).stem}.log"

                with open(log_path, "w") as log_file:
                    log_file.write(stdout.decode())
                    log_file.write(stderr.decode())

                return jsonify({
                    "arguments": data,
                    "report_file": report_filename,
                    "log_file": log_path.name
                })
            except Exception as e:
                return jsonify({"error": str(e)}), 500

        @self.app.route("/results/<path:filename>", methods=["GET"])
        def get_results_json(filename):
            sanitized_path = self._sanitize_report_path_filename(filename)
            if not sanitized_path or not sanitized_path.exists():
                return jsonify({"error": "File not found"}), 404
            return send_file(str(sanitized_path), mimetype="application/json")

        @self.app.route("/logs/<path:filename>", methods=["GET"])
        def get_logs(filename):
            logs_dir = Path(self.options.report_directory) / "logs"
            sanitized_path = (logs_dir / filename).resolve()
            try:
                sanitized_path.relative_to(logs_dir)
            except ValueError:
                return jsonify({"error": "Invalid path"}), 400

            if not sanitized_path.exists():
                return jsonify({"error": "Log file not found"}), 404
            return send_file(str(sanitized_path), mimetype="text/plain")

    def _sanitize_report_path_filename(self, report_path_filename):
        report_dir = Path(self.options.report_directory)
        resolved_path = (report_dir / report_path_filename).resolve()
        try:
            resolved_path.relative_to(report_dir)
        except ValueError:
            return False
        return resolved_path

    def run(self):
