        context.load_cert_chain(self.options.ssl_cert, self.options.ssl_key)
        self.app.run(
            host=self.options.host,
            port=self.options.port,
            ssl_context=context
        )

    def start_api_server(self):
        process = Process(target=self.run)
        process.start()

