import sys
import time
import json
import logging
import xml.etree.ElementTree as ET
from functools import wraps
from io import StringIO
from flask import Flask, request, jsonify, Response, Blueprint
from flask_cors import CORS, cross_origin

class FlaskWithExceptionFormatting(Flask):
    def handle_exception(self, e):
        exc_type, exc_value, exc_tb = sys.exc_info()
        response = jsonify({
            'error': str(e),
            'type': exc_type.__name__
        })
        response.status_code = 500
        return response

class Server:
    def __init__(self, data=None, formats=None, authorization=None, allow_profiler=False, profiler_output=None, profile_by_default=False, allow_add=False, logfile=sys.stdout, loglevel='WARNING', log_exception_formatter=None):
        self.data = data or {}
        self.app = FlaskWithExceptionFormatting(__name__)
        self.port = 6363
        self.setup_logging(logfile, loglevel, log_exception_formatter)
        self.formats = formats or ['json']
        self.parsers = {
            'json': self.parse_json,
            'xml': self.parse_xml
        }
        self.serializers = {
            'json': self.serialize_json,
            'xml': self.serialize_xml
        }
        self.authorization = authorization
        self.allow_profiler = allow_profiler
        self.profiler_output = profiler_output
        self.profile_by_default = profile_by_default
        self.api_blueprint = Blueprint('api', __name__)
        CORS(self.api_blueprint)
        self.setup_routes()
        self.app.register_blueprint(self.api_blueprint)

    def setup_logging(self, logfile, loglevel, log_exception_formatter):
        self.logger = logging.getLogger(__name__)
        self.logger.setLevel(loglevel)
        handler = logging.StreamHandler(logfile)
        formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s')
        handler.setFormatter(log_exception_formatter if log_exception_formatter else formatter)
        self.logger.addHandler(handler)
        self.app.logger.addHandler(handler)

    def setup_routes(self):
        @self.api_blueprint.route('/comp', methods=['POST', 'HEAD', 'OPTIONS'])
        @cross_origin()
        def compserver():
            if request.method == 'HEAD':
                return Response(status=200)
            if request.method == 'OPTIONS':
                return Response(status=204)
            try:
                if self.authorization:
                    auth_header = request.headers.get('Authorization')
                    if not (callable(self.authorization) and self.authorization(auth_header)) if callable(self.authorization) else auth_header == self.authorization:
                        raise Exception("Unauthorized")
                content_type = request.headers.get('Content-Type')
                parser = self.get_parser(content_type)
                data = parser(request.data)
                result = self.process_computation(data)
                accept = request.headers.get('Accept', 'application/json')
                serializer = self.get_serializer(accept)
                response_data = serializer(result)
                return Response(response_data, content_type=accept)
            except Exception as e:
                self.logger.exception("Error processing computation request")
                return jsonify(error=str(e)), 500

        if self.allow_profiler:
            from cProfile import Profile
            from pstats import Stats
            import functools

            def profiler_decorator(f):
                @functools.wraps(f)
                def wrapper(*args, **kwargs):
                    pr = Profile()
                    result = pr.runcall(f, *args, **kwargs)
                    s = StringIO()
                    ps = Stats(pr, stream=s).sort_stats('cumulative')
                    ps.print_stats()
                    with open(self.profiler_output, 'a') as f:
                        f.write(s.getvalue())
                    return result
                return wrapper

            if self.profile_by_default:
                view_func = compserver.view_func
                compserver.view_func = profiler_decorator(view_func)

    def get_parser(self, content_type):
        if not content_type:
            raise Exception("Missing Content-Type header")
        for fmt in self.formats:
            if fmt in content_type.lower():
                return self.parsers[fmt]
        raise Exception(f"Unsupported Content-Type: {content_type}")

    def get_serializer(self, accept_header):
        if not accept_header:
            return self.serializers.get('json')
        for fmt in self.formats:
            if fmt in accept_header.lower():
                return self.serializers[fmt]
        raise Exception(f"Unsupported Accept format: {accept_header}")

    def parse_json(self, data):
        try:
            return json.loads(data.decode('utf-8'))
        except json.JSONDecodeError as e:
            raise Exception(f"Invalid JSON: {e}")

    def parse_xml(self, data):
        try:
            root = ET.fromstring(data)
            return {child.tag: child.text for child in root}
        except ET.ParseError as e:
            raise Exception(f"Invalid XML: {e}")

    def serialize_json(self, data):
        return json.dumps(data)

    def serialize_xml(self, data):
        root = ET.Element('response')
        for k, v in data.items():
            child = ET.SubElement(root, k)
            child.text = str(v)
        return ET.tostring(root, encoding='unicode')

    def process_computation(self, data):
        return {'result': data, 'status': 'processed'}

    def run(self, port=6363, retry=False, **kwargs):
        self.port = port
        while True:
            try:
                self.logger.info(f"Starting server on port {port}")
                self.app.run(port=port, **kwargs)
                break
            except OSError as e:
                if retry and 'Address already in use' in str(e):
                    self.logger.warning(f"Port {port} is in use, retrying in 1 second...")
                    time.sleep(1)
                else:
                    self.logger.error(f"Failed to start server: {e}")
                    raise

