        host = proxy_host if proxy_host else self._server
        port = proxy_port if proxy_port else self._port

        if self._ssl_enabled:
            context = self._get_ssl_context()
            self._conn = http.client.HTTPSConnection(host, port, context=context) if context else http.client.HTTPSConnection(host, port)
        else:
            self._conn = http.client.HTTPConnection(host, port)

        if proxy_host and self._ssl_enabled:
            self._conn.set_tunnel(self._server, self._port)

    def _get_proxy_config(self):
        proto = 'https' if self._ssl_enabled else 'http'
        proxy_env = os.environ.get(f'{proto}_proxy')
        if not proxy_env:
            return (None, None)
        parsed = urlparse(proxy_env)
        host = parsed.hostname
        port = parsed.port or (443 if parsed.scheme == 'https' else 8080)
        return (host, port)

    def _get_ssl_context(self):
        if self._ssl_verification_enabled:
            return ssl.create_default_context()
        else:
            context = ssl.SSLContext(ssl.PROTOCOL_TLS_CLIENT)
            context.check_hostname = False
            context.verify_mode = ssl.CERT_NONE
            return context

    def _isconnected(self):
        return self._conn and self._conn.sock is not None

    def _reconnect(self):
        self._tries += 1
        if self._tries > self.maxtries:
            raise Exception("Max retries exceeded")
        time.sleep(self._delay)
        self._delay *= 2
        try:
            if self._conn:
                self._conn.close()
            self._connect()
        except Exception as e:
            raise e

    def _reset_retries(self):
        self._tries = 0
        self._delay = 1

    def write(self, data, reconnect_on=(502, 503, 504)):
        attempts = 0
        while attempts <= self.maxtries:
            try:
                if not self._isconnected():
                    self._reconnect()
                self._conn.putrequest('POST', self._url)
                headers = self._headers.copy()
                headers['Transfer-Encoding'] = 'chunked'
                for k, v in headers.items():
                    self._conn.putheader(k, v)
                self._conn.endheaders()

                self._conn.send(f"{len(data):X}\r\n".encode())
                self._conn.send(data)
                self._conn.send(b"\r\n")

                resp = self._conn.getresponse()
                if resp.status in reconnect_on:
                    self._reconnect()
                    attempts += 1
                else:
                    self._reset_retries()
                    break
            except (socket.error, http.client.HTTPException) as e:
                self._reconnect()
                attempts += 1

    def close(self):
        if not self._closed and self._conn:
            try:
                self._conn.putrequest('POST', self._url)
                for k, v in self._headers.items():
                    self._conn.putheader(k, v)
                self._conn.putheader('Transfer-Encoding', 'chunked')
                self._conn.endheaders()
                self._conn.send(b"0\r\n\r\n")
                resp = self._conn.getresponse()
