from flask import Flask, request, render_template, jsonify

app = Flask(__name__)
VERIFY_TOKEN = 'YOUR_VERIFY_TOKEN'

@app.route('/webhook', methods=['GET'])
def handle_verification():
    token = request.args.get('hub.verify_token')
    challenge = request.args.get('hub.challenge')
    if token == VERIFY_TOKEN:
        return challenge
    return render_template('verify_failed.html'), 403

@app.route('/webhook', methods=['POST'])
def handle_messages():
    data = request.get_json()
    event_type = get_type_from_payload(data)
    if event_type == 'message':
        for sender_id, msg_type, content in messaging_events(data):
            print(f"Message: {sender_id} - {msg_type}: {content}")
    elif event_type == 'postback':
        for sender_id, payload in postback_events(data):
            print(f"Postback: {sender_id} - {payload}")
    return 'ok', 200

def get_type_from_payload(data):
    try:
        entry = data['entry'][0]
        event = entry['messaging'][0]
        return 'message' if 'message' in event else 'postback'
    except (KeyError, IndexError):
        return None

def postback_events(data):
    for entry in data.get('entry', []):
        for event in entry.get('messaging', []):
            if 'postback' in event:
                yield event['sender']['id'], event['postback']

def messaging_events(data):
    for entry in data.get('entry', []):
        for event in entry.get('messaging', []):
            if 'message' in event:
                msg = event['message']
                sender = event['sender']['id']
                if 'text' in msg:
                    yield sender, 'text', msg['text']
                elif 'attachments' in msg:
                    for att in msg['attachments']:
                        yield sender, att.get('type'), att.get('payload', {}).get('url')
                elif 'quick_reply' in msg:
                    qr = msg['quick_reply']
                    yield sender, 'quick_reply', qr.get('payload')
                else:
                    yield sender, 'unknown', None

