import dnstwist.core

class DNSTwistAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.sessions = []
        self.session_timeout = 3600
        self.cleanup_interval = 600
        self.sessions_lock = threading.Lock()
        self._setup_routes()
        self._start_janitor()

    def _start_janitor(self):
        def janitor():
            while True:
                time.sleep(self.cleanup_interval)
                now = time.time()
                with self.sessions_lock:
                    self.sessions[:] = [
                        s for s in self.sessions
                        if now - s['start_time'] < self.session_timeout
                    ]
        threading.Thread(target=janitor, daemon=True).start()

    def _setup_routes(self):
        @self.app.route('/')
        def root():
            return "DNSTwist API", 200

        @self.app.route('/api/scan', methods=['POST'])
        def api_scan():
            data = request.get_json()
            domain = data.get('domain')
            if not domain:
                return jsonify({'error': 'Domain is required'}), 400

            sid = str(uuid.uuid4())
            session = {
                'sid': sid,
                'domain': domain,
                'status': 'running',
                'start_time': time.time(),
                'permutations': [],
                'registered_domains': [],
                'should_stop': False
            }

            with self.sessions_lock:
                self.sessions.append(session)

            thread = threading.Thread(target=self.run_scan, args=(session,))
            thread.start()

            return jsonify({'sid': sid}), 202

        @self.app.route('/api/status/<sid>')
        def api_status(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            return jsonify({'status': session['status']})

        @self.app.route('/api/domains/<sid>')
        def api_domains(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed yet'}), 400
            return jsonify(session['registered_domains'])

        @self.app.route('/api/csv/<sid>')
        def api_csv(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed yet'}), 400

            si = StringIO()
            cw = csv.writer(si)
            cw.writerow(['domain'])
            for domain in session['registered_domains']:
                cw.writerow([domain])
            csv_data = si.getvalue()

            headers = {
                'Content-Type': 'text/csv',
                'Content-Disposition': f'attachment; filename={sid}.csv'
            }
            return csv_data, 200, headers

        @self.app.route('/api/json/<sid>')
        def api_json(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed yet'}), 400
            return jsonify(session['registered_domains'])

        @self.app.route('/api/list/<sid>')
        def api_list(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed yet'}), 400
            return '\n'.join(session['registered_domains']), 200, {'Content-Type': 'text/plain'}

        @self.app.route('/api/stop/<sid>', methods=['POST'])
        def api_stop(sid):
            with self.sessions_lock:
                session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            session['should_stop'] = True
            session['status'] = 'stopped'
            return jsonify({'message': 'Session stopped'})

    def run_scan(self, session):
        try:
            permutations = dnstwist.core.permute(session['domain'])
            permutation_domains = [p['domain-name'] for p in permutations]
            session['permutations'] = permutation_domains
            registered = []
            for p in permutations:
                if session.get('should_stop'):
                    break
                if dnstwist.core.is_registered(p['domain-name']):
                    registered.append(p['domain-name'])
            session['registered_domains'] = registered
            if session.get('should_stop'):
                session['status'] = 'stopped'
            else:
                session['status'] = 'completed'
        except Exception as e:
            session['status'] = 'failed'
            session['error'] = str(e)
        finally:
            session['end_time'] = time.time()

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port, threaded=True)

if __name__ == '__main__':
    api = DNSTwistAPI()
    api.run()

