from flask import Flask, abort, Response
from pygments import highlight
from pygments.lexers import get_lexer_for_filename
from pygments.lexers.special import TextLexer
from pygments.formatters import HtmlFormatter

if len(sys.argv) != 2:
    print("Usage: python app.py <directory>")
    sys.exit(1)
BASE_DIR = sys.argv[1]
app = Flask(__name__)

@app.route('/<slug>')
def beautify(slug):
    if '..' in slug or slug.startswith('/'):
        abort(404)
    dir_path = os.path.join(BASE_DIR, slug)
    if not os.path.isdir(dir_path):
        abort(404)
    files = []
    try:
        for f in os.listdir(dir_path):
            file_path = os.path.join(dir_path, f)
            if os.path.isfile(file_path):
                files.append(file_path)
    except Exception:
        abort(404)
    if not files:
        abort(404)
    code_file = files[0]
    try:
        with open(code_file, 'r') as f:
            code = f.read()
    except Exception:
        abort(404)
    try:
        lexer = get_lexer_for_filename(code_file, code)
    except Exception:
        lexer = TextLexer()
    formatter = HtmlFormatter()
    html_output = highlight(code, lexer, formatter)
    return Response(f'<html><body>{html_output}</body></html>', mimetype='text/html')

