import os
from flask import Flask, request, abort
import hashlib
import hmac
import base64
import logging
from linebot import WebhookHandler
from linebot.exceptions import InvalidSignatureError

app = Flask(__name__)
app.config['LINE_CHANNEL_SECRET'] = os.getenv('LINE_CHANNEL_SECRET', 'default_secret')
handler = WebhookHandler(app.config['LINE_CHANNEL_SECRET'])

@app.route('/callback', methods=['POST'])
def callback():
    signature = request.headers.get('X-Line-Signature')
    body_bytes = request.get_data()
    try:
        body = body_bytes.decode('utf-8')
    except UnicodeDecodeError:
        app.logger.error('Failed to decode body')
        return abort(400)

    secret = app.config['LINE_CHANNEL_SECRET'].encode('utf-8')
    hash = hmac.new(secret, body_bytes, hashlib.sha256).digest()
    computed_signature = base64.b64encode(hash).decode()

    if signature != computed_signature:
        app.logger.error('Invalid signature: %s', signature)
        return abort(400)

    app.logger.info('Request body: %s', body)

    try:
        handler.handle(body, signature)
    except InvalidSignatureError:
        app.logger.error('Webhook handler signature mismatch')
        return abort(400)
    except Exception as e:
        app.logger.error('Processing error: %s', e, exc_info=True)
        return abort(400)

    return 'OK'

