        reader = csv.DictReader(csv_data)
        try:
            texts = [row['text'] for row in reader]
        except KeyError:
            return jsonify({'error': 'CSV must contain text column'}), 400

        preds = [score(text) for text in texts]
        output = StringIO()
        writer = csv.writer(output)
        for p in preds:
            writer.writerow(p)
        return Response(output.getvalue(), mimetype='text/csv')

    elif content_type == 'text/plain':
        lines = request.data.decode('utf-8').splitlines()
        texts = [line.strip() for line in lines if line.strip()]
        if not texts:
            return jsonify({'error': 'No valid text lines provided'}), 400
        preds = [score(text) for text in texts]
        output = StringIO()
        writer = csv.writer(output)
        for p in preds:
            writer.writerow(p)
        return Response(output.getvalue(), mimetype='text/csv')

