logger = logging.getLogger("http_request")
logger.setLevel(logging.DEBUG)

def make_request(method, action, param=None, **params):
    if param is None:
        param = {}

    logger.debug("Request: method=%s, action=%s", method, action)

    proxy_host = params.get('proxy_host')
    proxy_port = params.get('proxy_port')
    proxy_user = params.get('proxy_user')
    proxy_pass = params.get('proxy_pass')
    headers = params.get('headers', {})
    timeout = params.get('timeout', 30)

    parsed_url = urlparse(action)
    scheme = parsed_url.scheme
    netloc = parsed_url.netloc
    path = parsed_url.path or '/'
    query = parsed_url.query

    if ':' in netloc:
        host, port = netloc.split(':', 1)
        port = int(port)
    else:
        host = netloc
        port = 443 if scheme == 'https' else 80

    if proxy_host and scheme == 'https':
        logger.warning("HTTPS over proxy not supported; connecting directly")
        proxy_host = None

    conn_class = http_client.HTTPSConnection if scheme == 'https' else http_client.HTTPConnection
    use_proxy = bool(proxy_host and scheme == 'http')

    if use_proxy:
        conn_host, conn_port = proxy_host, proxy_port or 8080
        request_path = action
        if proxy_user and proxy_pass:
            import base64
            auth = base64.b64encode(('%s:%s' % (proxy_user, proxy_pass)).encode()).decode()
            headers['Proxy-Authorization'] = 'Basic %s' % auth
    else:
        conn_host, conn_port = host, port
        request_path = path
        if query:
            request_path += '?' + query

    body = None
    if method.lower() == 'get' and param:
        params_dict = dict([k_v.split('=') for k_v in query.split('&')]) if query else {}
        params_dict.update(param)
        request_path = path + '?' + '&'.join(['%s=%s' % (k, v) for k, v in params_dict.items()])
    elif method.lower() == 'post':
        if param:
            body = urlencode(param)
            if not isinstance(body, bytes):
                body = body.encode('utf-8')
            headers['Content-Type'] = 'application/x-www-form-urlencoded'
            headers['Content-Length'] = str(len(body))

    try:
        if scheme == 'https' and not use_proxy:
            import ssl
            context = ssl._create_unverified_context() if hasattr(ssl, '_create_unverified_context') else None
            conn = conn_class(conn_host, conn_port, context=context, timeout=timeout) if context else conn_class(conn_host, conn_port, timeout=timeout)
        else:
            conn = conn_class(conn_host, conn_port, timeout=timeout)

        conn.connect()
        conn.request(method.upper(), request_path, body=body, headers=headers)
        response = conn.getresponse()
        resp_data = response.read()

        if not isinstance(resp_data, str):
            resp_data = resp_data.decode('utf-8')

        logger.debug("Response status: %d", response.status)

        if not (200 <= response.status < 300):
            raise Exception("HTTP Error %d: %s - %s" % (response.status, response.reason, resp_data))

        return resp_data

    except http_client.HTTPException as e:
        logger.error("HTTPException: %s", e)
        raise
    except socket.timeout as e:
        logger.error("Socket timeout: %s", e)
        raise
    except Exception as e:
        logger.error("Request failed: %s", e)
        raise
    finally:
        try: conn.close()
        except: pass

