from urllib3.connection import HTTPConnection, HTTPSConnection
from urllib3.util.timeout import Timeout
from urllib3.util.retry import Retry
from urllib3.exceptions import MaxRetryError
from queue import LifoQueue, Empty, Full

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries if retries is not None else Retry.DEFAULT_METHOD
        self.pool = LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.conn_kw = conn_kw
        if headers:
            self.headers = headers.copy()
        else:
            self.headers = {}

    def _new_conn(self):
        conn = HTTPConnection(host=self.host, port=self.port, strict=self.conn_kw.get('strict', False), timeout=self.timeout, proxy=self.proxy, proxy_headers=self.proxy_headers, **self.conn_kw)
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=False)
        except Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=_Default, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, **response_kw):
        if retries is None:
            retries = self.retries
        redirect_count = 0
        while True:
            conn = self._get_conn(pool_timeout)
            try:
                if assert_same_host:
                    pass
                headers_copy = self.headers.copy()
                if headers:
                    headers_copy.update(headers)
                response = self._make_request(conn, method, url, body=body, headers=headers_copy, timeout=timeout)
                if redirect and response.status in (301, 302, 303, 307, 308):
                    redirect_count += 1
                    if redirect_count > retries.redirect:
                        raise MaxRetryError(self, url, "Redirects exceeded")
                    url = response.getheader('Location')
                    continue
                return response
            except Exception as e:
                retries = retries.increment(method, url, error=e)
                if not retries.is_retryable():
                    raise e
                continue
            finally:
                if release_conn or (release_conn is None and not getattr(conn, 'isclosed', False)()):
                    self._put_conn(conn)

    def close(self):
        while True:
            try:
                conn = self.pool.get(block=False)
                conn.close()
            except Empty:
                break

class HTTPSConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, key_file=None, cert_file=None, cert_reqs='CERT_REQUIRED', ca_certs=None, ssl_version='TLSv1', assert_hostname=None, assert_fingerprint=None, **kwargs):
        super().__init__(host, port, **kwargs)
        self.scheme = 'https'
        self.key_file = key_file
        self.cert_file = cert_file
        self.cert_reqs = cert_reqs
        self.ca_certs = ca_certs
        self.ssl_version = ssl_version
        self.assert_hostname = assert_hostname
        self.assert_fingerprint = assert_fingerprint

    def _prepare_conn(self, conn):
        conn.set_cert(key_file=self.key_file, cert_file=self.cert_file, cert_reqs=self.cert_reqs, ca_certs=self.ca_certs, ssl_version=self.ssl_version)
        if self.assert_hostname:
            conn.assert_hostname = self.assert_hostname
        if self.assert_fingerprint:
            conn.assert_fingerprint = self.assert_fingerprint
        return conn

    def _new_conn(self):
        conn = HTTPSConnection(host=self.host, port=self.port, strict=self.conn_kw.get('strict', False), timeout=self.timeout, proxy=self.proxy, proxy_headers=self.proxy_headers, **self.conn_kw)
        return self._prepare_conn(conn)

