class ExchangeRateAPI:
    def __init__(self):
        self.app = Sanic("ExchangeRateAPI")
        self.db = Gino()
        self.config = self._parse_config()
        self._setup_db()
        class ExchangeRates(self.db.Model):
            __tablename__ = 'exchange_rates'
            date = self.db.Column(self.db.Date, primary_key=True)
            rates = self.db.Column(self.db.JSONB)
        self.ExchangeRates = ExchangeRates
        self._setup_routes()

    def _parse_config(self):
        db_url = os.getenv('DATABASE_URL')
        parsed = urlparse(db_url)
        return {
            'host': parsed.hostname,
            'port': parsed.port,
            'user': parsed.username,
            'password': parsed.password,
            'database': parsed.path[1:] if parsed.path else ''
        }

    def _setup_db(self):
        db_config = {
            'dsn': os.getenv('DATABASE_URL'),
            'host': self.config['host'],
            'port': self.config['port'],
            'user': self.config['user'],
            'password': self.config['password'],
            'database': self.config['database'],
            'minsize': 1,
            'maxsize': 5,
            'echo': False,
            'pool_recycle': 3600
        }
        self.db.init_app(self.app, config=db_config)

    def _setup_routes(self):
        self.app.add_route(self.exchange_rates, '/exchange-rates', methods=['GET'])
        self.app.add_route(self.exchange_rates, '/exchange-rates/<date>', methods=['GET'])

    async def exchange_rates(self, request: Request, date=None):
        try:
            if date is None:
                latest_record = await self.ExchangeRates.query.order_by(
                    self.ExchangeRates.date.desc()
                ).first()
                if not latest_record:
                    return response.json({'error': 'No exchange rates found'}, status=404)
                record_date = latest_record.date
                record_rates = latest_record.rates
            else:
                try:
                    record_date = datetime.strptime(date, '%Y-%m-%d').date()
                except ValueError:
                    return response.json({'error': 'Invalid date format'}, status=400)
                record = await self.ExchangeRates.get(record_date)
                if not record:
                    return response.json({'error': 'Date not found'}, status=404)
                record_rates = record.rates

            base = request.args.get('base', 'EUR').upper()
            symbols = request.args.get('symbols', None)

            if base not in record_rates:
                return response.json({'error': 'Invalid base currency'}, status=400)

            base_rate = record_rates[base]
            converted_rates = {k: v / base_rate for k, v in record_rates.items()}

            if symbols:
                symbols_list = [s.strip().upper() for s in symbols.split(',')]
                converted_rates = {k: v for k, v in converted_rates.items() if k in symbols_list}

            return response.json({
                'base': base,
                'date': str(record_date),
                'rates': converted_rates
            })

        except Exception as e:
            return response.json({'error': 'Internal server error'}, status=500)

    def run(self, host="0.0.0.0", port=8000):
        self.app.run(host=host, port=port)

