import os
from collections import defaultdict
from typing import Optional, Union, List
from sanic import Sanic
from sanic.response import json as jsonify
from sanic.request import Request

class FamiliaAPI:
    def __init__(self, model_path: str, model_name: str, n_workers: int = None) -> None:
        self.app = Sanic(__name__)
        self.model_name = model_name
        self.model_path = model_path
        self.n_workers = n_workers
        self.model_dir = os.path.join(model_path, model_name)
        self.emb_file = os.path.join(self.model_dir, 'word_embeddings.bin')
        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, self.emb_file)
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, self.emb_file, model_type='slda')
        self.twe = TopicalWordEmbeddingsWrapper(self.model_dir, self.emb_file)
        self.lda_topic_words = self._read_topic_words_from_file()
        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words = defaultdict(list)
        file_path = os.path.join(self.model_dir, topic_words_file_name)
        with open(file_path, 'r', encoding='utf-8') as f:
            for line in f:
                parts = line.strip().split()
                if not parts:
                    continue
                topic_id = parts[0]
                words_scores = []
                for i in range(1, len(parts), 2):
                    if i + 1 < len(parts):
                        word = parts[i]
                        try:
                            score = float(parts[i + 1])
                            words_scores.append((word, score))
                        except ValueError:
                            continue
                topic_words[topic_id].extend(words_scores)
        return topic_words

    def _get_param(self, request: Request, param_name: str, default_value: Optional[Union[str, List[str]]] = None, is_list: bool = False) -> Union[str, List[str]]:
        value = request.args.get(param_name)
        if value is None:
            data = request.json or {}
            value = data.get(param_name)
        if value is None:
            value = default_value
        if is_list:
            if isinstance(value, list):
                return [self._strip_to_none(v) if isinstance(v, str) else v for v in value]
            elif isinstance(value, str):
                return [self._strip_to_none(v) for v in value.split(',')]
            else:
                return []
        return self._strip_to_none(value) if isinstance(value, str) else value

    def _strip_to_none(self, text: str) -> Optional[str]:
        if text is None:
            return None
        stripped = text.strip()
        return stripped if stripped else None

    def _response(self, success: bool = True, data=None, message: Optional[str] = None):
        return jsonify({
            'success': success,
            'data': data,
            'message': message
        }, status=200 if success else 400)

    def _error_response(self, message: str = 'Invalid request'):
        return self._response(success=False, message=message)

    def _setup_routes(self) -> None:
        @self.app.post('/tokenize')
        async def tokenize(request: Request):
            text = self._get_param(request, 'text')
            if not text:
                return self._error_response('Missing text parameter')
            tokens = self._tokenize(text)
            return self._response(data={'tokens': tokens})

        @self.app.post('/infer-lda')
        async def infer_lda(request: Request):
            tokens = self._get_param(request, 'tokens', is_list=True)
            if not tokens:
                return self._error_response('Missing tokens parameter')
            try:
                result = self.inference_engine_lda.infer(tokens)
                formatted_result = []
                for topic_id, score in result.items():
                    formatted_result.append({
                        'topic_id': topic_id,
                        'score': score,
                        'words': self.lda_topic_words.get(topic_id, [])
                    })
                return self._response(data=formatted_result)
            except Exception as e:
                return self._error_response(str(e))

    def run(self) -> None:
        self.app.run(host='0.0.0.0', port=8000, workers=self.n_workers or 1)

    def _tokenize(self, text: str) -> List[str]:
        return text.split()

