from typing import Union, List, Any, Optional, Callable, Tuple
from urllib.parse import urlparse
from http.client import HTTPConnection, HTTPSConnection

class JSONRPCExecutor:
    def __init__(self, command: Union[str, List[str]], url: str, jsonrpc_method: str, jsonrpc_params: Optional[List[Any]] = None, status: str = "^2\\d\\d$", result_validator: Optional[Callable[[Any], Tuple[bool, Optional[str]]]] = None, verify_tls: bool = True, method: str = "POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params or []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn: Union[HTTPConnection, HTTPSConnection]) -> None:
        parsed_url = urlparse(self.url)
        path = parsed_url.path or "/"
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": 1
        }
        body = json.dumps(payload).encode('utf-8')
        headers = {
            "Content-Type": "application/json",
            "Content-Length": len(body)
        }
        conn.request(self.method, path, body=body, headers=headers)

    def _validate_response(self, response) -> bool:
        if not re.match(self.status, str(response.status)):
            return False

        content_type = response.getheader("Content-Type")
        if not content_type or "application/json" not in content_type:
            return False

        try:
            data = json.loads(response.read().decode())
        except json.JSONDecodeError:
            return False

        if "error" in data:
            return False

        if self.result_validator:
            is_valid, _ = self.result_validator(data.get("result"))
            if not is_valid:
                return False

        return True

    def execute(self) -> bool:
        parsed_url = urlparse(self.url)
        hostname = parsed_url.hostname
        port = parsed_url.port or (443 if parsed_url.scheme == "https" else 80)

        if parsed_url.scheme == "https":
            if self.verify_tls:
                conn = HTTPSConnection(hostname, port)
            else:
                ssl_context = ssl.create_default_context()
                ssl_context.check_hostname = False
                ssl_context.verify_mode = ssl.CERT_NONE
                conn = HTTPSConnection(hostname, port, context=ssl_context)
        else:
            conn = HTTPConnection(hostname, port)

        try:
            self._send_request(conn)
            http_response = conn.getresponse()
            return self._validate_response(http_response)
        except Exception:
            return False
        finally:
            conn.close()

