        try:
            process = subprocess.Popen(comm, shell=shell, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        except OSError:
            return ("", "Command not found!", 1)
        stdout_queue = queue.Queue()
        stderr_queue = queue.Queue()
        stdout_thread = threading.Thread(target=self._read_output, args=(process.stdout, stdout_queue))
        stderr_thread = threading.Thread(target=self._read_output, args=(process.stderr, stderr_queue))
        stdout_thread.start()
        stderr_thread.start()
        process.wait()
        stdout_thread.join()
        stderr_thread.join()
        stdout_lines = []
        while not stdout_queue.empty():
            line = stdout_queue.get_nowait()
            stdout_lines.append(self._decode(line))
        stdout = '\n'.join(stdout_lines)
        stderr_lines = []
        while not stderr_queue.empty():
            line = stderr_queue.get_nowait()
            stderr_lines.append(self._decode(line))
        stderr = '\n'.join(stderr_lines)
        returncode = process.returncode
        return (stdout, stderr, returncode)

