            else:
                decomp_cmd = [compression, '-d']
            p_decompress = subprocess.Popen(decomp_cmd, stdin=p_cat.stdout, stdout=subprocess.PIPE, stderr=subprocess.PIPE)

        stdin_source = p_decompress.stdout if p_decompress else p_cat.stdout
        p_info = subprocess.Popen(['partclone.info'], stdin=stdin_source, stdout=subprocess.PIPE, stderr=subprocess.PIPE)

        stdout_data, stderr_data = p_info.communicate()
        p_cat.wait()
        if p_decompress:
            p_decompress.wait()

        if p_cat.returncode != 0:
            return {'filesystem': '<unknown>'}
        if p_decompress and p_decompress.returncode != 0:
            return {'filesystem': '<unknown>'}
        if p_info.returncode != 0:
            return {'filesystem': '<unknown>'}

        try:
            output = stdout_data.decode('utf-8')
            parsed_info = Partclone.parse_partclone_info_output(output)
        except:
            return {'filesystem': '<unknown>'}

        if 'filesystem' not in parsed_info:
            return {'filesystem': '<unknown>'}
        return parsed_info

    @staticmethod
    def parse_partclone_info_output(output):
        parsed = {}
        if not output:
            return parsed
        for line in output.splitlines():
            line = line.strip()
            if not line or ':' not in line:
                continue
            key, value = line.split(':', 1)
            parsed[key.strip()] = value.strip()
        return parsed

