    while cleaned and cleaned[0] == '':
        cleaned.pop(0)
    while cleaned and cleaned[-1] == '':
        cleaned.pop()
    final = []
    for line in cleaned:
        if line == '' and final and final[-1] == '':
            continue
        final.append(line)
    return '\n'.join(final)

