        self.__service_url = service_url
        self.__url = urlparse(service_url) if service_url else None
        self.__id_count = 0
        self.__auth_header = None
        self.timeout = timeout
        self.__conn = connection

        if self.__conn is None:
            if not self.__url and btc_conf_file:
                raise NotImplementedError("btc_conf_file parsing not implemented in this example")
            if not self.__url:
                raise ValueError("service_url must be provided")

            username = self.__url.username
            password = self.__url.password
            if username is not None and password is not None:
                auth_str = f"{username}:{password}".encode('utf-8')
                self.__auth_header = b"Basic " + base64.b64encode(auth_str)

            host = self.__url.hostname
            port = service_port if service_port is not None else self.__url.port

            if self.__url.scheme == 'https':
                self.__conn = httplib.HTTPSConnection(host, port, timeout=self.timeout)
            else:
                self.__conn = httplib.HTTPConnection(host, port, timeout=self.timeout)

    def _call(self, service_name, *args):
        self.__id_count += 1
        request_body = json.dumps({
            "method": service_name,
            "params": args,
            "id": self.__id_count
        })
        headers = {"Content-Type": "application/json"}
        if self.__auth_header:
            headers["Authorization"] = self.__auth_header

        try:
            self.__conn.request("POST", self.__url.path, body=request_body, headers=headers)
        except socket.error as e:
            raise ConnectionError(f"Request failed: {e}")

        return self._get_response()

    def _get_response(self):
        try:
            response = self.__conn.getresponse()
        except socket.error as e:
            raise ConnectionError(f"Response failed: {e}")

        if response.status != 200:
            raise Exception(f"HTTP {response.status}: {response.reason}")

        try:
            result = json.loads(response.read().decode('utf-8'))
        except json.JSONDecodeError:
            raise Exception("Invalid JSON response")

        if 'error' in result and result['error']:
            raise Exception(f"RPC Error: {result['error']}")

        if 'id' not in result or result['id'] != self.__id_count:
            raise Exception("Response ID mismatch")

        return result.get('result')

    def close(self):
        if self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()

