            response = client.post(
                "https://api-free.deepl.com/v2/translate",
                headers=headers,
                data=data
            )
    except httpx.RequestError:
        return None
    if response.status_code == 429:
        raise TooManyRequestsException()
    if response.status_code != 200:
        return None
    try:
        json_response = response.json()
    except ValueError:
        return None
    translations = json_response.get("translations", [])
    if not translations:
        return None
    main_text = translations[0].get("text", "")
    alternatives = []
    if "alternatives" in translations[0]:
        alternatives = [alt.get("text", "") for alt in translations[0].get("alternatives", [])]
    if printResult:
